package ex001;

public class ex4_5_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 23;
		int y = 22;
		//judge the odd and even number
		if(x%2==0){
			System.out.println("x is even number!"+(x%2));
		}else{
			System.out.println("x is odd number!"+(x%2));
		}
		if(y%2==0){
			System.out.println("y is even number!"+(y%2));
		}else{
			System.out.println("y is odd number!"+(y%2));
		}
		
	}

}
